
public class Nested {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int a=40,b=50,c=30;
		
		if (a>b && a>c){
			
			System.out.println("A is greater");
		}
		
		else
			if (b>a && b>c){
			
				System.out.println("B is greater");
		}
		
			else{
				
				System.out.println("c is greater");
			}

	}

	
}
